/**
 * This class represents the event that gets dispatched by the
 * PluginManager.
 */
export default class Event {
  /**
   * Create a new instance.
   *
   * @param {EventConfig} config         event configuration
   * @param {String}      config.name    event name
   * @param {Object}      config.context event context
   */
  constructor(config) {
    this._name = config.name || 'event';
    this._context = config.context;
  }

  /**
   * Get event name.
   *
   * @returns {String} the event name
   */
  get name() {
    return this._name;
  }

  /**
   * Get event context.
   *
   * @returns {Object} the event context
   */
  get context() {
    return this._context;
  }

  /**
   * Set event context.
   *
   * @param {Object} context the event context
   *
   * @returns {Event} this instance
   */
  set context(context) {
    this._context = context;

    return this;
  }
}

import Promise from 'bluebird';
import path from 'path';
import fs from 'fs-extra';
import writeJsonFile from 'write-json-file';
import childProcess from 'child_process';
import log from 'color-logger';
import readPackage from 'read-pkg-up';
import Event from './Event';
import validator from './util/schemaValidator';

const readFileAsync = Promise.promisify(fs.readFile);

/**
 * Utility class used by the PluginManager to discover and load plugins.
 */
export default class Npm {
  /**
   * Create a new instance.
   *
   * @param {Object} config config object
   */
  constructor(config) {
    this._debug = config.debug || false;

    log.debug = this._debug;
  }

  /**
   * Generates a plugin list.
   *
   * @param {String} manager  event context
   * @param {String} filePath path to new file
   *
   * @returns {Object} generated config
   */
  async generateConfig(manager, filePath) {
    log.i('generateConfig()');

    const configPath = filePath || '.plugged-in.json';

    try {
      const obj = await readPackage();

      if (typeof obj.pkg['plugged-in'] === 'undefined') {
        throw new Error('package.json must have a `plugged-in` section definining the context');
      }

      const sysConfig = obj.pkg['plugged-in'];

      const configObj = {
        context: sysConfig.context,
        plugins: [],
      };

      const modules = await this.getModules();

      await Promise.mapSeries(modules, async (dir) => {
        try {
          const plugPkg = await Npm.findPackage(dir);

          if (plugPkg === null) {
            return;
          }

          const config = plugPkg['plugged-in'];

          if (typeof config !== 'undefined') {
            if (config.context === sysConfig.context) {
              const name = plugPkg.name;

              if (name === obj.pkg.name) {
                return;
              }

              await validator.validate(
                'http://plugged-in.x-c-o-d-e.com/schema/configuration+v1#',
                config
              );

              let plugin = { name };

              delete config.context;

              plugin = Object.assign(plugin, config);

              configObj.plugins.push(plugin);
            }
          }
        } catch (error) {
          log.e('process modules', error.message, dir);
        }
      });

      manager.addPlugins(configObj.plugins);

      const event = new Event({ name: 'generateConfig', context: configObj });

      manager.emit(event.name, event);

      await writeJsonFile(configPath, event.context, { indent: 2 });

      return configObj;
    } catch (error) {
      log.e('generateConfig()', error.message);
    }

    return null;
  }

  /**
   * Find package.json file.
   *
   * @param {String} [dir] optional directory to search
   *
   * @returns {Object} package
   */
  static async findPackage(dir) {
    const directory = dir || __dirname;
    let packageObj = null;

    try {
      const packageFilePath = path.join(directory, 'package.json');

      const exists = fs.existsSync(packageFilePath);

      if (exists === false) {
        return packageObj;
      }

      const json = await readFileAsync(packageFilePath, { encode: 'utf8' });

      packageObj = JSON.parse(json);
    } catch (error) {
      log.w('findPackage()', error.message);
    }

    return packageObj;
  }

  /**
   * Execute shell command.
   *
   * @param {String} command command line command
   *
   * @returns {String} output
   */
  async executeShell(command) {
    const bufferSize = 1024 * 500;

    const execAsync = Promise.promisify(childProcess.exec);

    try {
      return await execAsync(command, { maxBuffer: bufferSize });
    } catch (error) {
      log.e(
        error.message
          .replace('Command failed: npm ls --parseable', '')
          .replace('npm ERR! ', '')
      );

      return null;
    }
  }

  /**
   * Get installed modules.
   *
   * @returns {String[]} list of all module directories
   *
   * @private
   */
  async getModules() {
    let modules = [];

    try {
      let result = await this.executeShell('npm ls --parseable');

      modules = this._prepareModuleList(result);

      result = await this.executeShell('npm ls -g --parseable');

      modules = modules.concat(this._prepareModuleList(result));
    } catch (error) {
      log.e(error);
    }

    return modules;
  }

  /**
   * Prepares module list from shell output.
   *
   * @param {String} data shell output
   *
   * @returns {String[]} list of modules
   *
   * @private
   */
  _prepareModuleList(data) {
    const modules = [];

    data
      .split('\n')
      .forEach((dir) => {
        if (dir.trim() !== '') {
          modules.push(dir);
        }
      });

    return modules;
  }
}

import Promise from 'bluebird';
import fs from 'fs';
import { EventEmitter } from 'events';
import log from 'color-logger';
import Npm from './Npm';
import Event from './Event';

const fileSystem = Promise.promisifyAll(fs);

/**
 * This class is usually the main part of the application.
 */
export default class PluginManager extends EventEmitter {
  /**
   * Create a new instance.
   *
   * @param {Object}  [options]               options object
   * @param {Boolean} [options.debug]         debug flag
   * @param {String}  [options.context]       context name
   * @param {String}  [options.configFile]    set the config file path
   * @param {Boolean} [options.override=true] whether to override matching functions
   */
  constructor(options = {}) {
    log.i('new PluginManager()', options);

    super(options);

    this._debug = options.debug || false;
    this._override = options.override || true;
    this._configFile = options.configFile || '.plugged-in.json';
    this._context = options.context;

    log.debug = options.debug || false;
  }

  /**
   * Get the current application context.
   *
   * @returns {String} the context name
   */
  get context() {
    return this._context;
  }

  /**
   * Determine if running in debug mode.
   *
   * @returns {Boolean} true or false
   */
  get debug() {
    return this._debug;
  }

  /**
   * Get logger.
   *
   * @returns {Object} the logger instance
   */
  get log() {
    return log;
  }

  /**
   * Initialize the application.
   *
   * Generates config file if it does not exist and loads plugins.
   *
   * @param {Object} plugin local event handlers
   * @param {Object} [npm]  optional Npm instance
   *
   * @returns {PluginManager} this plugin
   */
  async init(plugin = {}, npm = new Npm({ debug: this._debug })) {
    log.i('PluginManager.init()');

    let data;

    try {
      const exists = fileSystem.existsSync(this._configFile);

      if (exists === true) {
        data = await fileSystem.readFileAsync(this._configFile);

        data = JSON.parse(data);

        await this.addPlugins(data.plugins);
      } else {
        log.d('.plugged-in.json does not exists');

        data = await npm.generateConfig(this);
      }

      if (typeof this._context === 'undefined') {
        this._context = data.context;
      }
    } catch (error) {
      log.e('PluginManager.init()', error.message);
    }

    // Add local plugin last so that it can override externals if necessary
    await this.addPlugins([plugin]);

    this.dispatch('postInit', this);

    return this;
  }

  /**
   * Helper method to dispatch events.
   *
   * @param {String} eventName the event name
   * @param {Object} [context] optional event context object
   *
   * @returns {Object} the modified data object
   */
  async dispatch(eventName, context) {
    if (typeof eventName === 'undefined') {
      throw new Error('Event name not provided');
    }

    if (typeof context === 'undefined') {
      context = {};
    }

    const event = new Event({ name: eventName, context });

    log.i(`Dispatching ${event.name}...`);

    this.emit(eventName, event);

    return context;
  }

  /**
   * Determines if a handler exists for an event.
   *
   * @param {String}          event   the event name
   * @param {Function|String} handler the function or its name
   *
   * @returns {Boolean} true if exists, otherwise false
   */
  hasHandler(event, handler) {
    const listeners = this.listeners(event)
      .map((listener) => listener.name);

    if (typeof handler === 'function') {
      return listeners.indexOf(handler.name) !== -1;
    }

    return listeners.indexOf(handler) !== -1;
  }

  /**
   * Determines if an event has any handlers.
   *
   * @param {String} event the event name
   *
   * @returns {Boolean} true or false
   */
  hasHandlers(event) {
    if (typeof event === 'undefined') {
      throw new Error('Undefined event name');
    }

    return this.listeners(event).length > 0;
  }

  /**
   * Removes a handler for an event that matches the handler function name.
   *
   * @param {String}          event   the event name
   * @param {Function|String} handler the function or its name
   *
   * @returns {PluginManager} this instance
   */
  removeHandler(event, handler) {
    let name = handler;

    if (typeof name === 'function') {
      name = handler.name;
    }

    this.listeners(event).forEach((listener) => {
      if (listener.name === name) {
        this.removeListener(event, listener);
      }
    });

    return this;
  }

  /**
   * Add local plugins.
   *
   * @param {Object[]} plugins          list of plugins
   * @param {Object}   plugins.provides the provides object
   *
   * @returns {PluginManager} this instance
   */
  async addPlugins(plugins) {
    if (Array.isArray(plugins) === false) {
      throw new Error('Plugins must be an array');
    }

    await Promise.mapSeries(plugins, async (plugin) => this._processPlugin(plugin));

    return this;
  }

  /**
   * Add plugin handlers.
   *
   * @param {Object} plugin          the plugin
   * @param {Object} plugin.provides the provides object
   *
   * @returns {PluginManager} this instance
   *
   * @private
   */
  async _processPlugin(plugin) {
    try {
      if (typeof plugin.provides === 'undefined') {
        return this;
      }

      const pluginEvents = Object.keys(plugin.provides);

      await Promise.mapSeries(pluginEvents,
        async (eventName) => this._processEvent(plugin, eventName));
    } catch (error) {
      log.e(error.message);
    }

    return this;
  }

  /**
   * Add plugin event.
   *
   * @param {Object} plugin    the plugin
   * @param {String} eventName the event name
   *
   * @returns {PluginManager} this instance
   *
   * @private
   */
  async _processEvent(plugin, eventName) {
    try {
      let funcs = plugin.provides[eventName];

      if (Array.isArray(funcs) !== true) {
        funcs = [funcs];
      }

      await Promise.mapSeries(funcs, async (func) =>
        this._processHandler(plugin, eventName, func));
    } catch (error) {
      log.e(error.message);
    }

    return this;
  }

  /**
   * Add plugin handler.
   *
   * @param {Object}   plugin    the plugin
   * @param {String}   eventName the event name
   * @param {Function} handler   the handler
   *
   * @returns {PluginManager} this instance
   *
   * @private
   */
  async _processHandler(plugin, eventName, handler) {
    try {
      let func = handler;

      if (typeof handler === 'string') {
        func = await this._getCallback(plugin.name, handler);
      }

      if (func === null) {
        return this;
      }

      this._consolidateHandlers(eventName, func);

      this.on(eventName, func);
    } catch (error) {
      log.e(error.message);
    }

    return this;
  }

  /**
   * This removes existing matching handler for an event if necessary.
   *
   * @param {String}   eventName event name
   * @param {Function} handler   the handler
   *
   * @returns {PluginManager} this instance
   *
   * @private
   */
  _consolidateHandlers(eventName, handler) {
    if (this.hasHandler(eventName, handler) === true && this._override === true) {
      this.removeHandler(eventName, handler);
    }

    return this;
  }

  /**
   * Get callback for plugin service.
   *
   * @param {Object}   pluginName the plugin
   * @param {String}   funcName   the name of the event
   *
   * @returns {Function} the handler
   *
   * @private
   */
  async _getCallback(pluginName, funcName) {
    try {
      const pg = require(`${pluginName}`); // eslint-disable-line global-require

      return pg[funcName];
    } catch (error) {
      log.e(`Unsupported action '${funcName}'`, error);
    }

    return null;
  }
}

/**
 * The validation error class.
 *
 * Thrown by the SchemaValidator when an invalid object is passed into the `validate()` method.
 */
export default class ValidationError extends Error {
  /**
   * Create a new instance.
   *
   * @param {String} message the error message
   */
  constructor(message) {
    super(message);

    this.name = 'ValidationError';
  }

  /**
   * @returns {String} the error name
   */
  get name() {
    return this.name;
  }
}

import PluginManager from './PluginManager';
import Event from './Event';

export {
  PluginManager,
  Event,
};

import Ajv from 'ajv';
import ValidationError from './../errors/ValidationError';

const ajv = new Ajv({ allErrors: true });

ajv.addSchema(require('../../resources/schema/configuration+v1.json'));

export default {
  ajv,
  /**
   * Validate schema.
   *
   * @param {String} schema schema id
   * @param {Object} body   the object to validate
   *
   * @returns {Boolean} True on success and throws ValidationError on false
   *
   * @throws ValidationError
   */
  async validate(schema, body) {
    const result = await ajv.validate(schema, body);

    if (result !== true) {
      throw new ValidationError(ajv.errorsText());
    }

    return true;
  },
};

