import Promise from 'bluebird';
import fs from 'fs-extra';
import chai, { expect } from 'chai';
import Npm from './../src/Npm';

chai.use(require('dirty-chai'));

const copyAsync = Promise.promisify(fs.copy);
const removeAsync = Promise.promisify(fs.remove);
const readJson = Promise.promisify(fs.readJson);

describe('Npm', () => {
  let npm;

  beforeEach(() => {
    npm = new Npm({ debug: true });
  });

  it('gets created successfully', () => {
    expect(npm instanceof Npm).to.equal(true);
    expect(npm._debug).to.equal(true);
  });

  describe('findPackage', () => {
    it('finds a package.json successfully', async () => {
      const pkg = await Npm.findPackage(`${__dirname}/../`);

      expect(pkg.name).to.equal('plugged-in');
    });

    it('returns null if package not found', async () => {
      const pkg = await Npm.findPackage(__dirname);

      expect(pkg).to.equal(null);
    });
  });

  describe('executeShell', () => {
    it('executes ls command successfully', async () => {
      const result = await npm.executeShell('ls');

      const files = [];

      result
        .split('\n')
        .forEach((dir) => {
          if (dir.trim() !== '') {
            files.push(dir);
          }
        });

      expect(files.indexOf('src')).to.not.equal(-1);
      expect(files.indexOf('tests')).to.not.equal(-1);
      expect(files.indexOf('node_modules')).to.not.equal(-1);
    });
  });

  describe('getModules', () => {
    const modulesSdkPath = `${__dirname}/../node_modules/aws-sdk`;

    afterEach(async () => {
      await removeAsync(modulesSdkPath);
    });

    it('returns empty list if `extraneous` modules are installed', async () => {
      const sdkpath = `${__dirname}/fixtures/packages/aws-sdk`;

      await copyAsync(sdkpath, modulesSdkPath);

      const modules = await npm.getModules();

      expect(modules.length).to.equal(0);
    }).timeout(10000);

    it('gets list of installed modules successfully', async () => {
      const modules = await npm.getModules();

      expect(modules.length > 0).to.equal(true);
      expect(typeof modules[0]).to.equal('string');
    }).timeout(10000);
  });

  describe('generateConfig', async () => {
    const configPath = `${__dirname}/out/config.json`;

    afterEach(async () => {
      await removeAsync(configPath);
    });

    it('generates a new .plugged-in.json file successfully', async () => {
      const manager = {
        addPlugins: () => manager,
        emit: () => manager,
      };

      await npm.generateConfig(manager, configPath);

      const conf = await readJson(configPath);

      expect(conf.context).to.equal('plugged-in');
      expect(conf.plugins.length).to.equal(1);
    }).timeout(20000);
  });
});

import Moment from 'moment';
import chai, { expect } from 'chai';
import PluginManager from './../src/PluginManager';
import Event from './../src/Event';

chai.use(require('dirty-chai'));

describe('PluginManager', () => {
  let manager;
  let npm;

  const options = {
    context: 'test-context',
    debug: false,
  };

  beforeEach(() => {
    function onError() { }
    function setInitialisedAt() { }
    function setDefaultMaxHandlers() { }
    function cleanUp() { manager._events = {}; }

    manager = new PluginManager(options);
    npm = {
      generateConfig: (mgr) => {
        mgr._events = {
          error: onError,
          postInit: [setDefaultMaxHandlers, setInitialisedAt],
          exit: cleanUp,
        };

        return mgr;
      },
    };
  });

  it('gets created successfully', () => {
    expect(manager instanceof PluginManager).to.equal(true);
    expect(manager.context).to.equal(options.context);
    expect(manager.debug).to.equal(options.debug);
  });

  it('has logger', () => {
    expect(typeof manager.log._debug === 'boolean').to.equal(true);
  });

  describe('init', () => {
    it('gets initialised successfully', async () => {
      const result = await manager.init({}, npm);

      expect(result).to.deep.equal(manager);
      expect(typeof result._events.error).to.equal('function');
      expect(typeof result._events.postInit[0]).to.equal('function');
      expect(typeof result._events.postInit[1]).to.equal('function');
    }).timeout(10000);

    it('init with local event handlers', async () => {
      const onGenerateConfig = (event) => {
        event.context.createdAt = new Moment();
      };

      const plugin = {
        provides: {
          generateConfig: onGenerateConfig,
        },
      };

      const result = await manager.init(plugin, npm);

      expect(result).to.deep.equal(manager);
      expect(typeof result._events.generateConfig).to.equal('function');
      expect(typeof result._events.error).to.equal('function');
      expect(typeof result._events.postInit[0]).to.equal('function');
      expect(typeof result._events.postInit[1]).to.equal('function');
      expect(typeof result._events.exit).to.equal('function');
    });

    it('removes listeners on exit', async () => {
      const result = await manager.init({}, npm);

      const event = new Event({ name: 'exit', context: manager });

      manager.emit('exit', event);

      expect(result).to.deep.equal(manager);
      expect(Object.keys(result._events).length).to.equal(0);
    });

    it('local event handler overrides external', async () => {
      const setDefaultMaxHandlers = (event) => {
        const mgr = event.context;

        if (mgr) {
          mgr.setMaxListeners(10);
        }
      };

      const plugin = {
        provides: {
          postInit: setDefaultMaxHandlers,
        },
      };

      await manager.init(plugin, npm);

      const handlers = manager.listeners('postInit')
        .filter((handler) =>
          handler.name === 'setDefaultMaxHandlers');

      expect(handlers.length).to.equal(1);
      expect(manager._maxListeners).to.equal(10);
    });

    it('local event handler adds to external', async () => {
      const setDefaultMaxHandlers = (event) => {
        const mgr = event.context;

        if (mgr) {
          mgr.setMaxListeners(20);
        }
      };

      manager._override = false;

      const plugin = {
        provides: {
          postInit: setDefaultMaxHandlers,
        },
      };

      await manager.init(plugin, npm);

      const handlers = manager.listeners('postInit')
        .filter((handler) =>
          handler.name === 'setDefaultMaxHandlers');

      expect(handlers.length).to.equal(2);
      expect(manager._maxListeners).to.equal(20);
    });
  });

  describe('hasHandlers', () => {
    it('returns true if it has at least one handler for an event', async () => {
      await manager.init({}, npm);

      expect(manager.hasHandlers('postInit')).to.equal(true);
    });

    it('returns false if it does not have any handlers for an event', async () => {
      await manager.init({}, npm);

      expect(manager.hasHandlers('preInit')).to.equal(false);
    });

    it('throw error if hasHandlers() not passed an event name', async () => {
      await manager.init({}, npm);

      try {
        manager.hasHandlers();
      } catch (error) {
        expect(error.message).to.equal('Undefined event name');
      }
    });
  });

  describe('dispatch', () => {
    it('throws error if event name is not provided', async () => {
      try {
        await manager.init({}, npm);

        await manager.dispatch();
      } catch (error) {
        expect(error.message).to.equal('Event name not provided');
      }
    });

    it('dispatch event without context object should work', async () => {
      const config = { debug: true, start: 0 };

      const getConfig = (event) => {
        event.context.config = config;
      };

      const plugin = {
        provides: {
          preExec: getConfig,
        },
      };

      await manager.init(plugin, npm);

      const result = await manager.dispatch('preExec');

      expect(result.config).to.deep.equal(config);
    });
  });
});

import chai, { expect } from 'chai';
import ValidationError from './../../src/errors/ValidationError';

chai.use(require('dirty-chai'));

describe('ValidationError', () => {
  it('created successfully', () => {
    const message = 'my error';

    const error = new ValidationError(message);

    expect(error.name).to.equal('ValidationError');
    expect(error.message).to.equal(message);
  });

  it('allows setting custom message', () => {
    const newMessage = 'new message';

    const error = new ValidationError('');

    expect(error.message).to.not.equal(newMessage);

    error.message = newMessage;

    expect(error.message).to.equal(newMessage);
  });
});

import chai from 'chai';
import Ajv from 'ajv';
import validator from './../../src/util/schemaValidator';

const expect = chai.expect;

describe('schemaValidator', () => {
  const schema = 'http://plugged-in.x-c-o-d-e.com/schema/configuration+v1#';

  it('has Ajv instance as a property', () => {
    expect(validator.ajv instanceof Ajv).to.equal(true);
  });

  it('has all available schemas', () => {
    expect(Object.keys(validator.ajv._schemas).length).to.be.equal(2); // eslint-disable-line no-underscore-dangle, max-len
    expect(validator.ajv.getSchema('http://plugged-in.x-c-o-d-e.com/schema/configuration+v1#')).not.to.be.equal(undefined);
  });

  it('successfully validates correct schema', async () => {
    const object = {
      context: 'valid-context',
      provides: {
        init: 'onInit',
      },
    };

    await validator.validate(schema, object);
  });

  it('successfully validates invalid schema', async () => {
    try {
      const object = {
        context: 'valid-context',
      };

      await validator.validate(schema, object);
    } catch (error) {
      expect(error.message).to.equal('data should have required property \'provides\'');
    }
  });
});
